INSERT INTO `sw_question` VALUES ('46198', '6', '<p>案例13、一住宅小区施工现场，钢筋工王某正在使用钢筋切断机切割钢筋，由于切刀附近有些钢筋的断头，王某考虑到开关箱还有一些距离（约4m），为节省时间，王某直接用手去清理，结果导致4只手指被截断。</p>', '', '0', '14', 'C类（专职安全管理员）', '1', '0', '0');
